import React, { Component , PureComponent, useState } from 'react';
import { Container, Row, Col } from 'reactstrap';
import DateTime from "luxon";


function StationSummary(props) {

        const {station} = props;
        const station_length = station.length !== 0;
        return (
            <Row>
            {station_length ?
                    <Col>
                        <p> {station.Code} : {station.NameLong} </p>
                        <p> <b> First sampled: </b> {DateTime.fromISO(station.CreatedDate, "YYYY-MM-DDTHH:mm:ss.SSSSZ").toLocaleString(DateTime.DATE_FULL)} </p>
                        <p> <b> Most recently sampled: </b> {DateTime.fromISO(station.ModifiedDate, "YYYY-MM-DDTHH:mm:ss.SSSSZ").toLocaleString(DateTime.DATE_FULL)} </p>
                    </Col>

                    : <p> </p>

                }
            </Row>

        );
}

export default StationSummary;
