import React, { Component , PureComponent, useState } from 'react';
import { Container, Row, Col } from 'reactstrap';
import DateTime from "luxon";


function StationSummary(props) {

        const {station} = props;
        const station_length = station.length !== 0;
        return (
            <Row>
            {station_length ?
                    <Col>
                        <p> {station.Code} : {station.Description} </p>
                        <p> {Date(station.CreatedDate).toString()} </p>
                        <p> {Date(station.ModifiedDate).toString()} </p>
                    </Col>

                    : <p> </p>

                }
            </Row>

        );
}

export default StationSummary;
